        &LightingShader;
        
            struct OUTPUT
            {
                float4 diffuse                      : SV_TARGET0;
                float4 specular                     : SV_TARGET1;
            };

            OUTPUT MainPixelProgram(in INPUT kInput)
            {
                OUTPUT kOutput = (OUTPUT)0;
                GetLightingContribution(kInput, kOutput.diffuse.xyz, kOutput.specular.xyz);
                return kOutput;
            }
