filter.pixel:
        
            
        
        
        
            
            
            
        

        &LightingShader;
        
            struct OUTPUT
            {
                float4 screen                       : SV_TARGET0;
                float4 buffer                       : SV_TARGET1;
            };

            OUTPUT MainPixelProgram(in INPUT kInput) : SV_TARGET0
            {
                float3 nTotalDiffuse = 0.0f;
                float3 nTotalSpecular = 0.0f;
                GetLightingContribution(kInput, nTotalDiffuse, nTotalSpecular);

                OUTPUT kOutput;
                kOutput.screen = kOutput.buffer = float4((nTotalDiffuse + nTotalSpecular), 1.0f);
                return kOutput;
            }
