filter.pixel:
        
            Texture2D     gs_pAlbedoMap             : register(t0);

            float4 MainPixelProgram(in INPUT kInput) : SV_TARGET
            {
                float4 nAlbedo = gs_pAlbedoMap.Sample(gs_pLinearSampler, kInput.texcoord);

                [branch]
                if(nAlbedo.a < 0.5f)
                {
                    discard;
                }

                return 0.0f;
            }
